import React, { useEffect, useState } from 'react';

interface Props {}

const ThemeToggler = (props: Props) => {
  const [theme, setTheme] = useState<"light" | "dark">("light");

  useEffect(() => {
    if (theme === "dark") {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  return (
    <div className="flex items-center">
      <button onClick={toggleTheme} className="focus:outline-none">
        {theme === "light" ? (
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6 text-yellow-500"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
          </svg>
        ) : (
          <svg
            xmlns="http://www.w3.org/2000/svg"
            className="h-6 w-6 text-indigo-900"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M13 21h2a2 2 0 002-2V5a2 2 0 00-2-2h-1l-5 7V5a2 2 0 00-2-2H7a2 2 0 00-2 2v14a2 2 0 002 2h2l5-7v7z"
            />
          </svg>
        )}
      </button>
      <span className="ml-2 text-gray-600 dark:text-gray-400">{theme === "light" ? "Dark" : "Light"}</span>
    </div>
  );
};

export default ThemeToggler;
