import React, { useEffect, useState } from 'react';

interface Props {}

const ThemeToggler = (props: Props) => {
  const [theme, setTheme] = useState<"light" | "dark">("light");

  useEffect(() => {
    if (theme === "dark") {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  return (
    <div className="flex items-center">
      <button onClick={toggleTheme} className="focus:outline-none">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6 text-yellow-400 dark:text-gray-400"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          {theme === "light" ? (
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 6v6m0 0v6m0-6h6m-6 0H6"
            />
          ) : (
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 18l-8.293-8.293a1 1 0 0 1 1.414-1.414L12 15.586l7.293-7.293a1 1 0 0 1 1.414 1.414L12 18z"
            />
          )}
        </svg>
      </button>
      <span className="ml-2 text-gray-800 dark:text-gray-300">
        {theme === "light" ? "Light" : "Dark"}
      </span>
    </div>
  );
};

export default ThemeToggler;
