import React, { useState } from "react";
import AreaCard from "../../components/info/AreaCard";
import { areas } from "../../data/Areas";

const lightColors = ["bg-blue-200", "bg-green-200", "bg-yellow-200", "bg-purple-200", "bg-pink-200", "bg-indigo-200"];
const darkColors = ["bg-blue-800", "bg-green-800", "bg-yellow-800", "bg-purple-800", "bg-pink-800", "bg-indigo-800"];

const InfoPage = () => {
  const [theme, setTheme] = useState<"light" | "dark">("light");

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  const colors = theme === "light" ? lightColors : darkColors;

  return (
    <div className="container mx-auto px-4 py-8 h-screen">
      <div className="h-[80vh] overflow-y-auto rounded-lg border border-gray-300 shadow-md">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 p-4">
          {areas.map((area, index) => {
            // Используем остаток от деления на длину массива цветов для выбора по порядку
            const colorIndex = index % colors.length;
            return (
              <AreaCard
                key={index}
                category={area.category}
                name={area.name}
                fgp={area.fgp}
                relief={area.relief}
                climate={area.climate}
                internalWaters={area.internalWaters}
                soils={area.soils}
                landscape={area.landscape}
                vegetation={area.vegetation}
                fauna={area.fauna}
                bg_color={colors[colorIndex]} // Передаем цвет из соответствующего массива
              />
            );
          })}
        </div>
      </div>
      <button onClick={toggleTheme} className="fixed bottom-4 right-4 p-2 rounded-full bg-gray-200 dark:bg-gray-800 text-gray-800 dark:text-gray-200">
        {theme === "light" ? "Dark Mode" : "Light Mode"}
      </button>
    </div>
  );
};

export default InfoPage;
