import React, { useState, useEffect } from "react";
import Tooltip from "./Tooltip";

interface Props {
  category: string;
  questionNumber: number;
  questionsAmount: number;
  questionText: string;
  options: { text: string; isCorrect: boolean }[];
}

const QuizCard: React.FC<Props> = ({
  category,
  questionNumber,
  questionsAmount,
  questionText,
  options,
}: Props) => {
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const [answerSelected, setAnswerSelected] = useState(false);

  useEffect(() => {
    shuffleOptions();
  }, [questionNumber]);

  const shuffleOptions = () => {
    const shuffledOptions = [...options].sort(() => Math.random() - 0.5);
    setSelectedOption(null);
    setShuffledOptions(shuffledOptions);
  };

  const [shuffledOptions, setShuffledOptions] = useState(
    [] as { text: string; isCorrect: boolean }[]
  );

  const handleOptionClick = (index: number, isCorrect: boolean) => {
    setSelectedOption(index);
    if (isCorrect) {
      // Действия при выборе правильного ответа
    } else {
      // Действия при выборе неправильного ответа
    }
  };

  const handleAnswerSelect = () => {
    setAnswerSelected(true);
  };

  // Константы для классов кнопок
  const correctClass = "bg-green-500 hover:bg-green-700 text-white";
  const incorrectClass = "bg-red-500 hover:bg-red-700 text-white";
  const defaultClass = "bg-blue-500 hover:bg-blue-700 text-white";

  // Константы для стилей текста и фона
  const lightTextClass = "text-gray-600";
  const darkTextClass = "dark:text-gray-400";
  const lightBgClass = "bg-gray-200";
  const darkBgClass = "dark:bg-gray-700";

  return (
    <div className="max-w-xl mx-auto bg-white dark:bg-black shadow-md rounded-lg overflow-hidden mb-4" style={{ height: '400px', overflowY: 'scroll' }}>
      <div className={`${lightBgClass} ${darkBgClass} ${lightTextClass} text-lg font-semibold px-6 py-4`}>
        {category}
      </div>
      <div className="p-6">
        <p className={`text-sm ${lightTextClass} ${darkTextClass} mb-2`}>
          Question {questionNumber} out of {questionsAmount}
        </p>
        <p className={`text-xl ${lightTextClass} ${darkTextClass} mb-4`}>{questionText}</p>
        <div className="grid grid-cols-1 gap-4">
          {shuffledOptions.map((option, index) => (
            <button
              key={index}
              onClick={() => handleOptionClick(index, option.isCorrect)}
              className={`py-2 px-4 rounded focus:outline-none focus:shadow-outline block w-full text-left ${
                selectedOption === index
                  ? option.isCorrect
                    ? correctClass
                    : incorrectClass
                  : defaultClass
              }`}
              disabled={answerSelected}
            >
              {option.text}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default QuizCard;
