import React, { useEffect, useState } from 'react';

interface Props {}

const ThemeToggler = (props: Props) => {
  const [theme, setTheme] = useState<"light" | "dark">("light");

  useEffect(() => {
    const root = document.documentElement;

    if (theme === "dark") {
      root.classList.add("dark");
    } else {
      root.classList.remove("dark");
    }

    // Добавление классов для анимации перехода
    root.classList.add("transition-colors", "duration-500");

    // Удаление классов для анимации перехода после завершения перехода
    setTimeout(() => {
      root.classList.remove("transition-colors", "duration-500");
    }, 500);
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  return (
    <div className="flex justify-center">
      <button
        onClick={toggleTheme}
        className={`w-12 h-6 rounded-full p-1 flex items-center transition-colors duration-500 ${
          theme === "light"
            ? "bg-indigo-300 dark:bg-purple-300"
            : "bg-gray-700 dark:bg-gray-600"
        }`}
      >
        <div
          className={`w-4 h-4 rounded-full transform transition-transform duration-500 ${
            theme === "light"
              ? "bg-yellow-400 translate-x-0"
              : "bg-white translate-x-6"
          }`}
        />
        <div
          className={`w-4 h-4 rounded-full transform transition-transform duration-500 ${
            theme === "light"
              ? "bg-white translate-x-2"
              : "bg-yellow-400 translate-x-[-1rem]"
          }`}
        />
      </button>
    </div>
  );
};

export default ThemeToggler;
