import React, { useState } from 'react';

const GuessNumber: React.FC = () => {
  const [secretNumber, setSecretNumber] = useState<number>(Math.floor(Math.random() * 100) + 1);
  const [guess, setGuess] = useState<string>('');
  const [message, setMessage] = useState<string>('');

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setGuess(event.target.value);
  };

  const handleGuess = () => {
    const guessedNumber: number = parseInt(guess, 10);
    if (isNaN(guessedNumber)) {
      setMessage('Введите число!');
    } else if (guessedNumber === secretNumber) {
      setMessage(`Поздравляем! Вы угадали число ${secretNumber}!`);
    } else if (guessedNumber < secretNumber) {
      setMessage('Загаданное число больше.');
    } else {
      setMessage('Загаданное число меньше.');
    }
    setGuess('');
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-semibold mb-4">Угадай число</h1>
      <p className="mb-4">Компьютер загадал число от 1 до 100. Попробуйте угадать его!</p>
      <input
        type="number"
        className="border border-gray-300 rounded px-4 py-2 mr-2"
        value={guess}
        onChange={handleInputChange}
        placeholder="Введите ваше предположение"
      />
      <button
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        onClick={handleGuess}
      >
        Попробовать
      </button>
      {message && <p className="mt-4">{message}</p>}
    </div>
  );
};

export default GuessNumber;
