import React, { useState } from 'react';

const GuessNumber: React.FC = () => {
  const [secretNumber, setSecretNumber] = useState<number>(generateRandomNumber());
  const [guess, setGuess] = useState<string>('');
  const [message, setMessage] = useState<string>('');
  const [theme, setTheme] = useState<'light' | 'dark'>('light');

  function generateRandomNumber(): number {
    return Math.floor(Math.random() * 100) + 1;
  }

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setGuess(event.target.value);
  };

  const handleGuess = () => {
    const guessedNumber: number = parseInt(guess, 10);
    if (isNaN(guessedNumber)) {
      setMessage('Введите число!');
    } else if (guessedNumber === secretNumber) {
      setMessage(`Поздравляем! Вы угадали число ${secretNumber}!`);
      setSecretNumber(generateRandomNumber());
    } else if (guessedNumber < secretNumber) {
      setMessage('Загаданное число больше.');
    } else {
      setMessage('Загаданное число меньше.');
    }
    setGuess('');
  };

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <div className={`min-h-screen flex justify-center items-center px-4 ${theme === 'dark' ? 'bg-gray-800 text-white' : 'bg-gray-100 text-gray-800'}`}>
      <div className={`max-w-lg w-full rounded-lg shadow-lg p-8 ${theme === 'dark' ? 'bg-gray-700' : 'bg-white'}`}>
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-3xl font-semibold">Угадай число</h1>
          <button
            className={`px-4 py-2 rounded-md transition-colors ${
              theme === 'dark'
                ? 'bg-gray-600 hover:bg-gray-500 text-white'
                : 'bg-blue-500 hover:bg-blue-700 text-white'
            }`}
            onClick={toggleTheme}
          >
            {theme === 'light' ? 'Темная тема' : 'Светлая тема'}
          </button>
        </div>
        <p className="mb-4">Компьютер загадал число от 1 до 100. Попробуйте угадать его!</p>
        <div className="flex items-center mb-4">
          <input
            type="number"
            className={`border px-4 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-500 rounded-l ${
              theme === 'dark'
                ? 'bg-gray-800 border-gray-600 text-white'
                : 'bg-white border-gray-300'
            }`}
            value={guess}
            onChange={handleInputChange}
            placeholder="Введите ваше предположение"
          />
          <button
            className={`px-4 py-2 rounded-r transition-colors ${
              theme === 'dark'
                ? 'bg-gray-600 hover:bg-gray-500 text-white'
                : 'bg-blue-500 hover:bg-blue-700 text-white'
            }`}
            onClick={handleGuess}
          >
            Попробовать
          </button>
        </div>
        {message && <p className="text-center">{message}</p>}
      </div>
    </div>
  );
};

export default GuessNumber;
