import React, { useEffect, useState } from 'react';

interface Props {}

const ThemeToggler = (props: Props) => {
  const [theme, setTheme] = useState<"light" | "dark">("light");

  useEffect(() => {
    if (theme === "dark") {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [theme]);

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  return (
    <div className="flex justify-center">
      <button
        onClick={toggleTheme}
        className={`relative w-12 h-6 rounded-full p-1 flex items-center transition-all duration-500 ${
          theme === "light"
            ? "bg-indigo-300 dark:bg-purple-300"
            : "bg-gray-700 dark:bg-gray-600"
        }`}
      >
        <div
          className={`absolute w-4 h-4 rounded-full bg-yellow-400 transition-transform duration-500 ${
            theme === "light" ? "left-0" : "left-full"
          }`}
        />
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className={`absolute top-1/2 left-1/2 w-4 h-4 text-gray-900 dark:text-gray-100 transform -translate-x-1/2 -translate-y-1/2 transition-transform duration-500 ${
            theme === "light" ? "translate-x-0" : "translate-x-3"
          }`}
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          {theme === "light" ? (
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M12 5v14M5 12h14"
            />
          ) : (
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M5 12h14"
            />
          )}
        </svg>
      </button>
    </div>
  );
};

export default ThemeToggler;
